import { useCallback, useMemo } from 'react';

// 사용자가 제공한 mp3 파일을 Base64 데이터 URL로 변환하여 직접 삽입합니다.
// 이 방법을 통해 외부 파일 로딩 오류를 근본적으로 해결합니다.
// 아래 플레이스홀더를 사용자가 변환한 실제 데이터 URL로 교체해야 합니다.
const TRUTH_SOUND_URL = 'data:audio/mpeg;base64,'; // 예: 'data:audio/mpeg;base64,SUQzBAAAAAAA...'

export const useGameSounds = () => {
  // 데이터 URL이 변경될 일이 없으므로 useMemo로 한 번만 생성합니다.
  const truthAudio = useMemo(() => {
    // 유효한 데이터 URL인지 간단히 확인합니다.
    if (TRUTH_SOUND_URL.startsWith('data:audio')) {
      return new Audio(TRUTH_SOUND_URL);
    }
    // 데이터가 없는 경우, 콘솔에 경고를 남기고 null을 반환합니다.
    console.warn("Sound Data URL is not provided. Sound effects will be disabled.");
    return null;
  }, []);

  const playSound = (audio: HTMLAudioElement | null) => {
    if (!audio) return; // 오디오 객체가 없으면 아무것도 하지 않습니다.

    // 사운드를 즉시 다시 재생할 수 있도록 초기화합니다.
    audio.pause();
    audio.currentTime = 0;
    audio.play().catch(error => {
      // 브라우저 정책 등으로 인한 재생 실패 시 콘솔에 에러를 기록합니다.
      console.error("Error playing sound:", error);
    });
  };

  // 모든 진실 선언 시 동일한 사운드를 재생합니다.
  const playRedTruthSound = useCallback(() => {
    playSound(truthAudio);
  }, [truthAudio]);

  const playBlueTruthSound = useCallback(() => {
    playSound(truthAudio);
  }, [truthAudio]);

  return { playRedTruthSound, playBlueTruthSound };
};
